
public class StringBufferExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		StringBuffer sb= new StringBuffer("abhilash");
		System.out.println(sb);
		
		sb.append("gaur");
		
		System.out.println(sb);
		
		sb.append("test");
		System.out.println(sb);

	}

}
